import logo from "./logo.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import { useEffect, useState, React } from "react";
import { getOneTodo, getTodos,addTodo, updateTodo, removeOneTodo } from "./services.js";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import Snackbar from "@mui/material/Snackbar";
import { Alert } from "./components/alert";
function App() {
  const [todos, setTodos] = useState([]);
  const [open, setOpen] = useState(false);
  const [addStatus, setAddStautus] = useState(true);
  const [currentTodo, setCurrentTodo] = useState({"content":""});
  const loadData = async () => {
    const callTodos = await getTodos();
    if (!callTodos) return;
    setTodos(callTodos);
  };
  const handleContent = (event)=>{
    const {value,name}= event.target;
    setCurrentTodo(preState=>{return {...preState,[name]:value}})

  }
  useEffect(() => {
    loadData();
  }, []);

  const addOrUpdate = async () => {
    let todo;
    let status= true;
    if(currentTodo.id){ 
      todo = await updateTodo(currentTodo.id,currentTodo);
      if(!todo) status= false;
      if (todo){ 
        setCurrentTodo(todo);
        loadData()
      }
    }
    else {
      todo = await addTodo(currentTodo);
      if(!todo) status= false; 
      if (todo){ setCurrentTodo(todo); setTodos(preState=> {return [...preState,todo] }) };
    }

    setOpen(true);
    setAddStautus(true);
  };
  const onKeyPress = (e)=>{
    if(e.key == 'Enter'){ addOrUpdate()} 
  }
  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  const handleDeleteOne=async(id)=>{
    await removeOneTodo(id)
    loadData()
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1> Work To-dos </h1>
      </header>
      <div className="container">
        <div className="input-group col-6 mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="New Item"
            aria-describedby="basic-addon1"
            name="content"
            onKeyPress={onKeyPress}
            onChange={handleContent}
          />
          <Button variant="outlined" onClick={addOrUpdate}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="16"
              height="16"
              fill="currentColor"
              className="bi bi-pencil"
              viewBox="0 0 16 16"
            >
              <path d="M12.146.146a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1 0 .708l-10 10a.5.5 0 0 1-.168.11l-5 2a.5.5 0 0 1-.65-.65l2-5a.5.5 0 0 1 .11-.168l10-10zM11.207 2.5 13.5 4.793 14.793 3.5 12.5 1.207 11.207 2.5zm1.586 3L10.5 3.207 4 9.707V10h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.293l6.5-6.5zm-9.761 5.175-.106.106-1.528 3.821 3.821-1.528.106-.106A.5.5 0 0 1 5 12.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.468-.325z" />
            </svg>
          </Button>
          <Stack spacing={2} sx={{ width: "100%" }}>
            <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
             {addStatus? <Alert
                onClose={handleClose}
                severity="success"
                sx={{ width: "100%" }}
              >
                Success
              </Alert>:
              <Alert
              onClose={handleClose}
              severity="error"
              sx={{ width: "100%" }}
            >
              Fail
            </Alert>
              }
            </Snackbar>
            {todos.map((todo,index) => {
              return <Alert key={index} severity="info" onClose={() => {handleDeleteOne(todo.id)}} icon={false} >{todo.content}</Alert>;
            })}
          </Stack>
        </div>
      </div>
    </div>
  );
}

export default App;
