import iconPencial from "./iconPencial.svg";
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";
import { useEffect, useState, React } from "react";
import { getOneTodo, getTodos } from "./services.js";
import Stack from "@mui/material/Stack";
import Button from "@mui/material/Button";
import Snackbar from "@mui/material/Snackbar";
import { Alert } from "./components/alert";
function App() {
  const [todos, setTodos] = useState([]);
  const [open, setOpen] = useState(false);
  const [addStatus,setAddStautus] = useState(true)
  const [currentTodo,setCurrentTodo] =useState({})
  const loadData = async () => {
    const callTodos = await getTodos();
    console.log(callTodos);
    if (!callTodos) return;
    setTodos(callTodos);
  };

  useEffect(() => {
    loadData();
  }, []);


  const addTodo = () => {
    setOpen(true);
    setAddStautus(true)
  };

  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }

    setOpen(false);
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1> Work To-dos </h1>
      </header>
      <div className="container">
        <div className="input-group col-6 mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="New Item"
            aria-describedby="basic-addon1"
          />
          <button className="btn btn-primary">
            <img src={iconPencial} />
          </button>
          <Stack spacing={2} sx={{ width: "100%" }}>
            <Button variant="outlined" onClick={addTodo}>
              Open success snackbar
            </Button>
            <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
              <Alert
                onClose={handleClose}
                severity="success"
                sx={{ width: "100%" }}
              >
                Add success
              </Alert>
            </Snackbar>
            <Alert severity="error">This is an error message!</Alert>
            {todos.map((todo) => {
              return <Alert severity="info">
                    {todo.content}
                  </Alert>;
                })
              }
          </Stack>
        </div>
      </div>
    </div>
  );
}

export default App;
